select a22.STORECD, max(피크수량) 
from (select a11.STORECD  STORECD,
sum(a11.SAL_QTY)  피크수량
from LGMJVDP.TB_STK_FT a11
where  a11.DATECD between '2020-07-01' and '2020-07-31'
and 
 a11.GOODCD in ('8801308445980', '8801308448578', '8801308448585', '8801308449384', '8801308450960', '2800014211267', '2800014211571', '2800014211595', '2800014211670', '2800014211694', '2800014510124', '2800014510285', '2800014510292', '2800014510704', '2800014510711', '2800014510742', '2800014510780', '2800014510797', '2800014510803', '2800014510810', '2800014510865', '2800014510896', '2800014510902', '2800014510919', '2800014510940', '2800047510047', '2800100008030', '2800100008993', '2800100036224', '2800100038020', '2800100038464', '2800100039782', '2800100043062', '2800100044762', '8801746502900', '8808024026882', '8808024028299', '8809695310362')
 and a11.STORECD in ('S355 ', '1904 ')
group by a11.STORECD, a11.DATECD
order by a11.storeCD)  a22
group by  a22.STORECD