		group by	a12.DATECD
		), 
	 gopa3 as
 (select	coalesce(pa11.DATECD, pa12.DATECD)  DATECD,
		((VALUE(pa11.WJXBFS1, 0) + VALUE(pa12.WJXBFS1, 0)) - VALUE(pa11.WJXBFS2, 0))  WJXBFS1
	from	gopa1	pa11
		full outer join	gopa2	pa12
		  on 	(pa11.DATECD = pa12.DATECD)
	where	coalesce(pa11.DATECD, pa12.DATECD) in ('2023-02-13', '2023-02-12', '2023-02-11', '2023-02-10')
